import React, { useState } from 'react';
import { getCatFact, getGif } from '../services/api';

const CurrentResult = () => {
  const [fact, setFact] = useState('');
  const [gifQuery, setGifQuery] = useState('');
  const [gifUrl, setGifUrl] = useState('');

  // Obtener el Cat Fact
  const fetchCatFact = async () => {
    try {
      const response = await getCatFact();
      setFact(response.data.fact);
    } catch (error) {
      console.error('Error al obtener Cat Fact:', error);
    }
  };

  // Obtener el GIF según la palabra clave
  const fetchGif = async () => {
    if (!gifQuery.trim()) {
      alert('Por favor ingresa una palabra clave.');
      return;
    }

    try {
      const response = await getGif(gifQuery.trim());
      setGifUrl(response.data.url);
    } catch (error) {
      console.error('Error al obtener el gif:', error);
    }
  };

  // Guardar los datos (esto suponiendo que se guarda automáticamente desde el backend cuando se accede a /history)
  const handleGuardar = async () => {
    try {
      if (!fact || !gifUrl) {
        alert('Por favor genera un Cat Fact y un Gif primero.');
        return;
      }
      // Suponiendo que el backend guarda los datos automáticamente al acceder a /history
      alert('Datos generados y guardados');
    } catch (error) {
      console.error('Error al guardar datos:', error);
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: 'auto', padding: '20px' }}>
      {/* 1. Botón y Cat Fact */}
      <button onClick={fetchCatFact} style={{ width: '100%', padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', marginBottom: '20px' }}>
        Traer Cat Fact
      </button>
      {fact && (
        <div style={{ marginTop: '10px', padding: '10px', border: '1px solid #ccc' }}>
          <strong>Fact:</strong> {fact}
        </div>
      )}

      {/* 2. Input de búsqueda de gif y botón */}
      <div style={{ marginTop: '20px' }}>
        <input
          type="text"
          placeholder="Palabra clave para el gif"
          value={gifQuery}
          onChange={(e) => setGifQuery(e.target.value)}
          style={{ width: '100%', padding: '8px' }}
        />
        <button onClick={fetchGif} style={{ width: '100%', marginTop: '10px', padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none' }}>
          Generar Gif
        </button>
      </div>

      {gifUrl && (
        <div style={{ marginTop: '10px' }}>
          <strong>URL del Gif:</strong> <a href={gifUrl} target="_blank" rel="noopener noreferrer">{gifUrl}</a>
        </div>
      )}

      {/* 3. Botón de Guardar */}
      <button onClick={handleGuardar} style={{ marginTop: '30px', backgroundColor: '#4CAF50', color: 'white', padding: '10px', width: '100%' }}>
        Guardar en historial
      </button>
    </div>
  );
};

export default CurrentResult;
