// src/App.js
import React, { useState } from 'react';
import CurrentResult from './components/CurrentResult';
import History from './components/History';
import './App.css';

function App() {
  const [showFact, setShowFact] = useState(false);
  const [showGif, setShowGif] = useState(false);
  const [updateTrigger, setUpdateTrigger] = useState(0); // para forzar actualización

  const handleShowFact = () => {
    setShowFact(true);
    setShowGif(false);
  };

  const handleShowGif = () => {
    setShowGif(true);
    setShowFact(false);
  };

  const handleUpdate = () => {
    setShowFact(true);
    setShowGif(true);
    setUpdateTrigger(prev => prev + 1); // cambia el trigger para que CurrentResult vuelva a hacer fetch
  };

  return (
    <div className="App">
      <h1>Cat Fact & Gif App</h1>

      <div className="button-group">
        <button onClick={handleShowFact}>Mostrar Cat Fact</button>
        <button onClick={handleShowGif}>Mostrar Gif</button>
        <button onClick={handleUpdate}>Actualizar</button>
      </div>

      <CurrentResult
        showFact={showFact}
        showGif={showGif}
        updateTrigger={updateTrigger}
      />

      <History />
    </div>
  );
}

export default App;
