import React, { useState, useEffect } from 'react';
import { getCatFact, getGif } from '../services/api';

const CurrentResult = () => {
  const [fact, setFact] = useState('');
  const [gifUrl, setGifUrl] = useState('');
  const [loading, setLoading] = useState(false);

  // Obtener el Cat Fact y luego el GIF automáticamente
  const fetchCatFactAndGif = async () => {
    try {
      setLoading(true);
      
      // 1. Obtener el hecho del gato
      const factResponse = await getCatFact();
      setFact(factResponse.data.fact);
      
      // 2. Extraer primeras 3 palabras para el GIF
      const query = factResponse.data.fact.split(' ').slice(0, 3).join(' ');
      
      // 3. Obtener el GIF
      const gifResponse = await getGif(query);
      setGifUrl(gifResponse.data.url);
      
    } catch (error) {
      console.error('Error:', error);
      alert('Error al obtener datos');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ 
      maxWidth: '600px', 
      margin: '20px auto', 
      padding: '20px',
      boxShadow: '0 0 10px rgba(0,0,0,0.1)',
      borderRadius: '8px'
    }}>
      <button 
        onClick={fetchCatFactAndGif}
        disabled={loading}
        style={{ 
          width: '100%', 
          padding: '12px',
          backgroundColor: loading ? '#cccccc' : '#4CAF50',
          color: 'white', 
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer',
          fontSize: '16px'
        }}
      >
        {loading ? 'Cargando...' : 'Generar Cat Fact y GIF'}
      </button>

      {fact && (
        <div style={{ 
          marginTop: '20px', 
          padding: '15px', 
          backgroundColor: '#f8f9fa',
          borderRadius: '4px'
        }}>
          <h3 style={{ marginTop: 0 }}>Dato sobre gatos:</h3>
          <p>{fact}</p>
        </div>
      )}

      {gifUrl && (
        <div style={{ marginTop: '20px' }}>
          <h3>GIF relacionado:</h3>
          <img 
            src={gifUrl} 
            alt="GIF relacionado con el hecho del gato"
            style={{ 
              maxWidth: '100%', 
              height: 'auto',
              border: '1px solid #ddd',
              borderRadius: '4px'
            }}
          />
          <div style={{ marginTop: '10px' }}>
            <a 
              href={gifUrl} 
              target="_blank" 
              rel="noopener noreferrer"
              style={{ color: '#4CAF50' }}
            >
              Abrir GIF en nueva pestaña
            </a>
          </div>
        </div>
      )}
    </div>
  );
};

export default CurrentResult;
