import React, { useState } from 'react';

const CurrentResult = () => {
  const [fact, setFact] = useState('');
  const [gifQuery, setGifQuery] = useState('');
  const [gifUrl, setGifUrl] = useState('');
  const [loadingFact, setLoadingFact] = useState(false);
  const [loadingGif, setLoadingGif] = useState(false);
  const [saving, setSaving] = useState(false);
  const [requestCount, setRequestCount] = useState(0); // Contador para refrescos

  // Validación de 3 palabras
  const validateQuery = () => {
    const words = gifQuery.trim().split(/\s+/).filter(word => word.length > 0);
    return words.length <= 3 && words.length > 0;
  };

  // Obtener Cat Fact
  const fetchCatFact = async () => {
    try {
      setLoadingFact(true);
      const response = await fetch('https://localhost:7270/api/catfact/fact');
      if (!response.ok) throw new Error('Error al obtener dato');
      setFact((await response.json()).fact);
    } catch (error) {
      alert(error.message);
    } finally {
      setLoadingFact(false);
    }
  };

  // Obtener/Refrescar GIF (misma función)
  const handleGifAction = async () => {
    if (!validateQuery()) {
      alert(gifQuery.trim() ? 'Máximo 3 palabras permitidas' : 'Ingresa palabras clave');
      return;
    }

    try {
      setLoadingGif(true);
      const response = await fetch(
        `https://localhost:7270/api/catfact/gif?query=${encodeURIComponent(gifQuery)}&v=${requestCount}`,
        { cache: 'no-store' } // Evitar caché
      );
      
      if (!response.ok) throw new Error('Error al obtener GIF');
      
      const data = await response.json();
      setGifUrl(data.url);
      setRequestCount(prev => prev + 1); // Incrementar para próximo refresco
    } catch (error) {
      alert(error.message);
    } finally {
      setLoadingGif(false);
    }
  };

  // Guardar en historial
  const handleGuardar = async () => {
    if (!fact || !gifUrl) {
      alert('Genera ambos datos primero');
      return;
    }

    try {
      setSaving(true);
      await fetch(`https://localhost:7270/api/catfact/save?fact=${encodeURIComponent(fact)}&query=${encodeURIComponent(gifQuery)}&gifUrl=${encodeURIComponent(gifUrl)}`);
      alert('¡Guardado exitoso!');
    } catch (error) {
      alert('Error al guardar');
    } finally {
      setSaving(false);
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
      {/* Sección Cat Fact */}
      <button 
        onClick={fetchCatFact} 
        disabled={loadingFact}
        style={{ padding: '10px', marginBottom: '10px' }}
      >
        {loadingFact ? 'Cargando...' : 'Obtener Dato de Gato'}
      </button>
      
      {fact && <div style={{ marginBottom: '20px' }}>{fact}</div>}

      {/* Sección GIF */}
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          value={gifQuery}
          onChange={(e) => setGifQuery(e.target.value)}
          placeholder="Buscar GIF (max 3 palabras)"
          style={{ width: '100%', padding: '8px' }}
        />
        <button 
          onClick={handleGifAction} 
          disabled={loadingGif}
          style={{ 
            marginTop: '10px',
            padding: '10px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none'
          }}
        >
          {gifUrl ? (loadingGif ? 'Buscando nuevo...' : 'Refrescar GIF') : (loadingGif ? 'Buscando...' : 'Generar GIF')}
        </button>
      </div>

      {/* Mostrar GIF */}
      {gifUrl && (
        <div style={{ marginBottom: '20px' }}>
          <img 
            src={gifUrl} 
            alt="GIF resultante" 
            style={{ maxWidth: '100%' }}
            key={requestCount} // Fuerza recarga al cambiar
          />
        </div>
      )}

      {/* Botón Guardar */}
      <button 
        onClick={handleGuardar}
        disabled={saving || !fact || !gifUrl}
        style={{ 
          padding: '10px',
          backgroundColor: '#2196F3',
          color: 'white',
          border: 'none'
        }}
      >
        {saving ? 'Guardando...' : 'Guardar en Historial'}
      </button>
    </div>
  );
};

export default CurrentResult;
