import React, { useState, useEffect } from 'react';
import { getCatFact, getGif } from '../services/api';

const CurrentResult = () => {
  const [fact, setFact] = useState('');
  const [gifUrl, setGifUrl] = useState('');
  const [showFact, setShowFact] = useState(true);

  const handleUpdate = async () => {
    try {
      // 1. Obtener el Cat Fact
      const factResponse = await getCatFact();
      const newFact = factResponse.data.fact;
      setFact(newFact);

      // 2. Obtener el gif basado en el Cat Fact
      const query = newFact.split(' ').slice(0, 3).join(' ');
      const gifResponse = await getGif(query);
      setGifUrl(gifResponse.data.url);
    } catch (error) {
      console.error("Error al actualizar datos:", error);
    }
  };

  // Llamada inicial al montar el componente
  useEffect(() => {
    handleUpdate();
  }, []);

  return (
    <div className="current-result">
      <div className="buttons-container">
        <button onClick={handleUpdate}>Nuevo Cat Fact + Gif</button>
        <button onClick={() => setShowFact(true)}>Ver Fact</button>
        <button onClick={() => setShowFact(false)}>Ver Gif</button>
      </div>

      {showFact ? (
        <div className="fact-container">
          <p>{fact}</p>
        </div>
      ) : (
        <div className="gif-container">
          {gifUrl ? (
            <img src={gifUrl} alt="Gif relacionado" />
          ) : (
            <p>No se encontr√≥ gif</p>
          )}
        </div>
      )}
    </div>
  );
};

export default CurrentResult;
