import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api'; // Ajusta el puerto si es diferente

export const getCatFact = () => axios.get(`${API_BASE_URL}/catfact/fact`);
export const getGif = (query) => axios.get(`${API_BASE_URL}/catfact/gif`, { params: { query } });
export const getHistory = () => axios.get(`${API_BASE_URL}/catfact/history`);
