import React, { useState, useEffect } from 'react';
import { getCatFact, getGif } from '../services/api';

const CurrentResult = () => {
  const [fact, setFact] = useState('');
  const [gifQuery, setGifQuery] = useState('');
  const [gifUrl, setGifUrl] = useState('');

  // Obtener un Cat Fact
  const fetchCatFact = async () => {
    try {
      const response = await getCatFact();
      setFact(response.data.fact);
    } catch (error) {
      console.error('Error al obtener cat fact:', error);
    }
  };

  // Obtener un GIF con la palabra clave escrita por el usuario
  const fetchGif = async () => {
    if (!gifQuery.trim()) {
      alert('Por favor, escribe una palabra clave para el gif.');
      return;
    }

    try {
      const response = await getGif(gifQuery.trim());
      setGifUrl(response.data.url);
    } catch (error) {
      console.error('Error al obtener gif:', error);
    }
  };

  // Guardar: obtener fact y gif
  const handleGuardar = async () => {
    await fetchCatFact();
    await fetchGif();
  };

  // Obtener un fact al cargar por primera vez
  useEffect(() => {
    fetchCatFact();
  }, []);

  return (
    <div className="current-result">
      <div className="inputs-container">
        <button onClick={fetchCatFact}>Traer Cat Fact</button>

        <input
          type="text"
          placeholder="Palabra clave para el gif"
          value={gifQuery}
          onChange={(e) => setGifQuery(e.target.value)}
        />

        <button onClick={handleGuardar}>Guardar</button>
      </div>

      <div className="fact-container">
        <h3>Cat Fact:</h3>
        <p>{fact}</p>
      </div>

      <div className="gif-container">
        <h3>Gif:</h3>
        {gifUrl ? (
          <img src={gifUrl} alt="Gif relacionado" />
        ) : (
          <p>No se ha buscado un gif a√∫n</p>
        )}
      </div>
    </div>
  );
};

export default CurrentResult;
