import axios from 'axios';
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import CurrentResult from './components/CurrentResult';
import History from './components/History';

const api = axios.create({
  baseURL: 'http://localhost:5270/api', // Asegúrate que coincide con tu puerto del backend
});

export const getCatFact = () => api.get('/fact');
export const getGif = (query) => api.get(`/gif?query=${encodeURIComponent(query)}`);
export const getHistory = () => api.get('/history');

const App = () => {
    return (
      <Router>
        <nav>
          <Link to="/">Resultado Actual</Link> | 
          <Link to="/history">Historial</Link>
        </nav>
        
        <Routes>
          <Route path="/" element={<CurrentResult />} />
          <Route path="/history" element={<History />} />
        </Routes>
      </Router>
    );
  };
  
  export default App;
