import axios from 'axios';

// Define la URL de tu API
const API_URL = 'http://localhost:5270/api/history'; // Asegúrate de ajustar esta URL según tu backend

// Función para obtener un Cat Fact desde el backend
export const getCatFact = async () => {
  try {
    const response = await axios.get('/api/catfact/fact'); // Ajusta esta URL según tu configuración
    return response;
  } catch (error) {
    console.error('Error obteniendo el Cat Fact', error);
    throw error;
  }
};

// Función para obtener un Gif relacionado desde el backend
const getGif = async (query) => {
    const apiUrl = `https://api.giphy.com/v1/gifs/search?api_key=tu_api_key&q=${query}&limit=1`;
    const response = await fetch(apiUrl);
    const data = await response.json();
    return data;  // Debería devolver un objeto que contenga la URL del gif
  };

// Función para obtener el historial de búsquedas
export const getHistory = async () => {
    try {
      const response = await axios.get(API_URL);
      return response;
    } catch (error) {
      console.error("Error al obtener historial:", error);
      throw error;
    }};
