import axios from 'axios';

const API_BASE = 'http://localhost:5000/api/catfact'; // Cambia el puerto si es necesario

export const getCatFact = () => axios.get(`${API_BASE}/fact`);
export const getGif = (query) => axios.get(`${API_BASE}/gif`, { params: { query } });
export const getHistory = () => axios.get(`${API_BASE}/history`);
